package it.unimi.di.ewlab.iss.common.model.actions;import android.view.KeyEvent;public class ButtonAction extends Action {    private String sourceId = "";    private String keyId = "";    private static final ActionType TYPE = ActionType.BUTTON;    ButtonAction() {        super(TYPE);    }    public ButtonAction(int actionId,String name, String sourceId, String keyId) {        super(actionId, name, TYPE);        this.sourceId = sourceId;        this.keyId = keyId;    }    public String getSourceId() {        return sourceId;    }    public void setSourceId(String sourceId) {        this.sourceId = sourceId;    }    public String getKeyId() {        return keyId;    }    public void setKeyId(String keyId) {        this.keyId = keyId;    }}